(async()=>{const e=async e=>{const t=`Bookmark ${e+1}`,o=prompt(`Enter the URL for ${t}`);if(o)if(window.sidebar)window.sidebar.addPanel(t,o,"");else if(window.opera&&window.print){const e=document.createElement("a");e.setAttribute("href",o),e.setAttribute("title",t),e.setAttribute("rel","sidebar"),e.click()}else document.all&&window.external.AddFavorite(o,t)};if(!window.location.pathname.includes("/course"))return alert("This script only works on the course pages.");for(;;){const t=prompt("How many bookmarks do you want to add?");if(!t)return;[...Array(Number(t)).keys()].forEach(e)}})();